mapper.select:
select user_idx, user_id, user_email, user_name, user_password, user_role
from users
where user_idx = #{user_idx}

select user_id
from users 
where user_email = #{user_email}

mapper.update:
update class
set user_email = #{user_email},
	user_name = #{user_name},
	user_password = #{user_password},
	user_role = #{user_role}
where user_idx = #{user_idx}
